s = "abcbmalayalamanc"
#s=""

length = 0
start = 0
for i in range(len(s)):
    substring = s[0:i+1]
    start = 0
    while len(substring) > length:
        if substring == substring[::-1]:
            length = len(substring)
            output = substring
            break
        else:
            start += 1
            substring = s[start:i+1]

print (length, output)


